import React, { isValidElement } from 'react';
import AliceCarousel from 'react-alice-carousel';
import 'react-alice-carousel/lib/alice-carousel.css';
import EventBox from './eventBoxes';
import { useState } from 'react';

const handleDragStart = (e) => e.preventDefault();

const items = [
  <EventBox
    eventName="Hack the box" 
    eventDescription="Sit in front of a box and pretend you know how to hack like you're mr.robot" 
    dpt="coding"
    time="2nd Nov 9:50"
  />,
 <EventBox 
    eventName="Relay Typing" 
    eventDescription="Pretend like you know how to code but you're just typing someone else's algorithm" 
    dpt="robotics"
    time="3rd Nov 1:00"
  />,
  <EventBox 
    eventName="Hack the box" 
    eventDescription="Sit in front of a box and pretend you know how to hack like you're mr.robot" 
    dpt="departmental"
    time="2nd Nov 9:50"
  />,
  <EventBox
    eventName="Hack the box" 
    eventDescription="Sit in front of a box and pretend you know how to hack like you're mr.robot" 
    dpt="coding"
    time="2nd Nov 9:50"
  />,
 <EventBox 
    eventName="Relay Typing" 
    eventDescription="Pretend like you know how to code but you're just typing someone else's algorithm" 
    dpt="robotics"
    time="3rd Nov 1:00"
  />,
  <EventBox 
    eventName="Hack the box" 
    eventDescription="Sit in front of a box and pretend you know how to hack like you're mr.robot" 
    dpt="departmental"
    time="2nd Nov 9:50"
  />,
];

// export default function EventCarousel () {
//   return (
//     <>
//     <div className='flex f justify-center  w-full h-[300px] '>
//       <div className='flex bg-[#0A0A0A]  place-items-center w-full h-full mt-0'>
//       <AliceCarousel mouseTracking items={items} responsive={{0: {items: 4}}} disableButtonsControls={true} disableDotsControls={true}/>
//       </div>
//     </div>
//     </>
//   );
// }
const thumbItems = (items, [setThumbIndex, setThumbAnimation]) => {
  return items.map((item, i) => (
      <div className="thumb" onClick={() => (setThumbIndex(i), setThumbAnimation(true))}>
          {item}
      </div>
  ));
};

export default function EventCarousel () {
  const [mainIndex, setMainIndex] = useState(0);
  const [mainAnimation, setMainAnimation] = useState(false);
  const [thumbIndex, setThumbIndex] = useState(0);
  const [thumbAnimation, setThumbAnimation] = useState(false);
  const [thumbs] = useState(thumbItems(items, [setThumbIndex, setThumbAnimation]));

  const slideNext = () => {
      if (!thumbAnimation && thumbIndex < thumbs.length - 1) {
          setThumbAnimation(true);
          setThumbIndex(thumbIndex + 1);
      }
  };

  const slidePrev = () => {
      if (!thumbAnimation && thumbIndex > 0) {
          setThumbAnimation(true);
          setThumbIndex(thumbIndex - 1);
      }
  };

  const syncMainBeforeChange = (e) => {
      setMainAnimation(true);
  };

  const syncMainAfterChange = (e) => {
      setMainAnimation(false);

      if (e.type === 'action') {
          setThumbIndex(e.item);
          setThumbAnimation(false);
      } else {
          setMainIndex(thumbIndex);
      }
  };

  const syncThumbs = (e) => {
      setThumbIndex(e.item);
      setThumbAnimation(false);

      if (!mainAnimation) {
          setMainIndex(e.item);
      }
  };

  return [
     <AliceCarousel
          activeIndex={mainIndex}
          animationType="fadeout"
          animationDuration={800}
          disableDotsControls
          disableButtonsControls
          items={items}
          mouseTracking={!thumbAnimation}
          onSlideChange={syncMainBeforeChange}
          onSlideChanged={syncMainAfterChange}
          touchTracking={!thumbAnimation}
     />,
     <div className="thumbs">
         <AliceCarousel
              activeIndex={thumbIndex}
              autoWidth
              disableDotsControls
              disableButtonsControls
              items={thumbs}
              mouseTracking={false}
              onSlideChanged={syncThumbs}
              touchTracking={!mainAnimation}
         />
         <div className="btn-prev" onClick={slidePrev}>&lang;</div>
         <div className="btn-next" onClick={slideNext}>&rang;</div>
     </div>
  ]
};
